import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'chatbot-frontend';
}

<style>
  :host {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";

    color: #333;
    box-sizing: border-box;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    --custom-send-color-green:#0ab39c;
    --custom-refresh-color-blue:#0e457e;
    --custom-chat-back-color:#f5f5f5;
  }

  ::-webkit-scrollbar {
  width: 5px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #f1f1f1;
}

/* Handle */
::-webkit-scrollbar-thumb {
  background: #888;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #555;
}

  .mainContainer{
    height: 100vh;
    overflow: hidden;
  }
  .contentContainer{
    height: 90%;
    background-color: var(--custom-chat-back-color);
    position: relative;
  }
  .navContainer{

    overflow: hidden;
    padding:1rem 15px;

  }
  .navPortions{
    font-size: smaller;

  }
  .mainIcon{
    display: flex;
    flex-direction: row;
    align-items: center;

  }

  .logoDiv{
    border-radius: 50%;
    background-color: #150ab3;
    padding: 5%;
    width: 2.5rem;
    height: 2.5rem;
    margin-right: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .logoDiv>image{
    width: 2;
  }
  .mainIconHeaders{
    display: flex;
    flex-direction: column;

  }

  .chatContainer{
    background-color: var(--custom-chat-back-color);
    height:85% ;

    overflow-x: hidden;
    overflow-y: scroll;

  }

  .chats{
    height: 50rem;
  }
  .queryContainer{
    background-color: rgb(252, 253, 253);
    height:fit-content;
    width: 100%;
    justify-content: space-evenly;
    position: absolute;
    bottom: 0;
    padding:0.5rem 15px;
  }

  .queryEntryContainer{

    width:85%
  }

  .queryEntryContainer{
    border-radius: 3px;

  }
  .queryButtons{

    display: flex;
    flex-direction: row;
  }
  .queryButtons>button{
    border: none;
    overflow: hidden;
    display: flex;
    outline: none;
    align-items: center;
    justify-content: center;
    height: 2rem;
    width: 2rem;
  }

  .iconImage{
    width: 1rem;
    height: 1rem;
  }

  .sendBtn{
    background-color: var(--custom-send-color-green);
  }

  .refreshBtn{
    background-color: var(--custom-refresh-color-blue) !important;
  }

  .queryInput{
    background-color: transparent;
    outline: none;
    width: 100%;
    border: none;
    font-size: 0.8rem;
  }
  /* Responsive Styles */
  @media screen and (max-width: 767px) {
    .card-container > *:not(.circle-link) ,
    .terminal {
      width: 100%;
    }

    .card:not(.highlight-card) {
      height: 16px;
      margin: 8px 0;
    }

    .card.highlight-card span {
      margin-left: 72px;
    }

    svg#rocket-smoke {
      right: 120px;
      transform: rotate(-5deg);
    }
  }

  @media screen and (max-width: 575px) {
    svg#rocket-smoke {
      display: none;
      visibility: hidden;
    }
  }
</style>


<div class="container-fluid mainContainer bg-body-secondary d-flex align-items-center justify-content-center ">
  <div class="container  contentContainer m-0 p-0" >
    <div class="container navContainer  m-0 p-1 bg-body-tertiary d-flex align-items-center justify-content-center" >
      <nav class="navbar navbar-expand-lg  navPortions col-12 p-0">
        <div class="container-sm m-0 ">
          <div class="mainIcon">
            <div class="logoDiv">
              <img src="assets/logo.png"/>
            </div>
            <div class="mainIconHeaders">
              <a class="navbar-brand  fw-medium lh-1 " style="font-size: 14px;" href="#">Salez.ai</a>
              <a class="navbar-brand  fw-lighter lh-1 " style="font-size: 11px;" href="#">Ai Bot</a>
            </div>

          </div>


        </div>
      </nav>
    </div>
    <div class="container chatContainer  d-flex  justify-content-center">

<div class="chats"></div>

    </div>
    <div class="container queryContainer m-0 d-flex align-items-center">
      <div class="queryEntryContainer m-0 p-1 bg-body-secondary">
        <input class="queryInput" type="text" placeholder="Ask me anything."/>
      </div >

      <div class="queryButtons">

          <button type="button " class="btn btn-primary m-1 sendBtn">
            <img class="iconImage" src="assets/send.png" alt="Send">
          </button>



          <button type="button " class="btn btn-primary m-1 refreshBtn">
            <img class="iconImage" src="assets/refresh.png" alt="Refresh">
          </button>

      </div> 


    </div>
  </div>
</div>

<router-outlet></router-outlet>
