<div class="container-fluid mainContainer bg-body-secondary d-flex align-items-center justify-content-center ">
  <div class="container  contentContainer  p-0" >
    <div class="container navContainer  m-0 p-1 bg-body-tertiary d-flex align-items-center justify-content-center" >
      <nav class="navbar navbar-expand-lg  navPortions col-12 p-0">
        <div class="container-sm m-0 ">
          <div class="mainIcon">
            <div class="logoDiv">
              <img src="assets/logo.png" alt="Logo"/>
            </div>
            <div class="mainIconHeaders" >
              <h4 class=" fw-medium "  >Salez.ai</h4>
              <h6 class=" fw-lighter"  >Ai Bot</h6>
            </div>

          </div>


        </div>
      </nav>
    </div>
    <div class="container chatContainer" id="chatContainer" >
      <p  *ngIf="showInitialMsg" class="initialmsg">Ask a question to start the conversation</p>

      <div class="chats">
        <div *ngFor="let data of chatData">
        <div class="chatDiv"  *ngIf="data.query.trim() !== ''">
          <div class="userQueryDiv">
            <div class="userQuery">
              <p>{{ data.query }}</p>
            </div>
            <p>{{ data.time }}</p>

          </div>

          <div class="response">
            <div class="responseLogo logoDiv">
              <img src="assets/logo.png" alt="Logo"/>
            </div>
            <div class="responseBox">
              <p *ngIf="data.response" [innerHTML]="data.response"></p>
              <app-loader class="Loader-class" *ngIf="!data.response"></app-loader>
            </div>
          </div>
        </div>
      </div>
    </div>


    </div>
    <div class="container queryContainer m-0 d-flex align-items-center">
      <div class="queryEntryContainer m-0 bg-body-secondary">
        <input class="queryInput" type="text" placeholder="Ask me anything." (keyup.enter)="sendQuery()" [(ngModel)]="inputValue" />
      </div>


      <div class="queryButtons">

          <button type="button " class="btn btn-primary m-1 sendBtn" (click)="sendQuery()">
            <img class="iconImage" src="assets/send.png" alt="Send">
          </button>



          <button type="button " class="btn btn-primary m-1 refreshBtn" (click)="refreshQuery()">
            <img class="iconImage" src="assets/refresh.png" alt="Refresh">
          </button>

      </div> 


    </div>
  </div>
</div>

